<template>
  <div>
    <div v-for="(card, index) in bankList" :key="index" class="cardRow bottom-line" @click="cardClicked(card, index)">
        <div class="flex-row align-center justify-between">
            <div class="flex-row align-center"> 
                <image :src="card.icon" class="icon-card"></image>
                <div class="flex-column align-start justify-center ml10">
                    <div class="flex-row align-center">
                        <text class="font-14 m-color-1">{{card.name}}</text>
                        <text class="font-14 m-color-1">{{card.num}}</text>
                        <image src="data:image/png;base64," class="icon-visa ml10" v-if="card.visa"></image>
                    </div>
                    <div class="flex-row align-center mt10">
                        <text v-if="choosenCardIndex === index" class="font-12 m-color-9">可用余额：{{card.balance}}元</text>
                        <text v-if="choosenCardIndex !== index" class="font-12 m-color-9">单笔限额{{card.tradeLimit}}万，单日限额{{card.dayLimit}}万</text>
                        <div class="card-tag ml20" v-if="card.special"> 
                            <text class="font-10 m-color-red">{{card.special}}</text>
                        </div>
                    </div>
                </div>
            </div>
            <image src="data:image/png;base64," class="icon-check" v-if="choosenCardIndex === index"></image>
        </div>
    </div>
  </div>
</template>

<script>
import Banks from './banks';
export default {
  props: {
    list:{
      type: Array,
      default: []
    }
  },
  data () {
    return {
      bankList:[],
      choosenCardIndex: 0
    }
  },
  methods:{ 
    cardClicked(item, index){
      this.choosenCardIndex = index;
      this.$emit('bankSelected', {item, index})
    }
  },
  created(){
    this.list.forEach((item, index) => {
      if(Banks[item.bank]){
        item.icon = Banks[item.bank]['icon'];
        item.name = Banks[item.bank]['name'];
      }else{
        item.icon = '';
        item.name = ''
      }
      this.bankList.push(item);
    })
  }
};
</script>
