
<template>
<div class="textfiled-input-container">
  <div class="textfiled-input-row">
    <!-- type=text -->
    <input
      class="textfiled-input"
      :maxlength="maxlength"
      :autofocus="autofocus"
      type="text"
      :name="name"
      :placeholder="placeholder"
      :disabled="disabled"
      v-model="currentValue"
      @focus="onFocus"
      @blur="onBlur"
      @return="onReturn"
      @input="onInput"
      @change="onChange"
      ref="input"/>

      <!-- clear -->
      <image class="textfiled-input__clear" @click="clear" v-if="isClearShow" src="data:image/png;base64,"></image>
  </div>
  <div class="textfiled-captcha-container">
    <text class="textfiled-captcha-text" v-if="!isCodeSend" @click="getCaptcha">获取验证码</text>
    <text class="textfiled-captcha-count" v-if="isCodeSend">{{captchaTime}}s</text>
  </div>
</div>

</template>


<script>
export default {
  name: 'captchafield',
  created () {
    this.currentValue = (this.value === undefined || this.value === null) ? '' :  this.value
  },
  data(){
    return { 
      currentValue: '',
      isClearShow: false,
      isCodeSend: this.isCodeClick,
      captchaTime: this.time,
      isDisabled: this.disabled
    }
  },
  props: {
    value: [String, Number],
    name: String,
    placeholder: String,
    disabled: {
        type: Boolean,
        default: false
    },
    autofocus: {
      type: Boolean,
      default: false
    },
    maxlength: Number,
    time:{
      type: Number,
      default: 90
    },
    isCodeClick:{
        type: Boolean,
        default: false
    }
  },
  methods: {
    clear () {
      this.currentValue = '';
      this.pwdValue = '';
    },
    focus () {
      this.$refs.input.focus()
    },
    blur () {
      this.$refs.input.blur()
    },
    onFocus ($event) {
      if(this.currentValue !== ""){
        this.isClearShow = true;
      }
      this.$emit('onFocus', this.currentValue, $event)
    },
    onBlur ($event) {
      this.isClearShow = false;
      this.$emit('onBlur', this.currentValue, $event)
    },
    //weex不支持keyup事件
    onReturn ($event) {
      if ($event.key === 'Enter') {
        $event.target.blur()
        this.$emit('onEnter', this.currentValue, $event)
      }
    },
    onInput($event) {
      this.$emit('onInput', this.currentValue, $event)
    },
    onChange($event) {
      this.$emit('onChange', this.currentValue, $event)
    },
    countDown(){
        this.isCodeSend = true;
        let time = setInterval(() => {
            if (this.captchaTime === 1) {
                clearInterval(time);
                this.isCodeSend = false;
                this.captchaTime = this.time;
            } else {
                this.captchaTime--;
            }
        }, 1000)
    },
    getCaptcha(){
        this.$emit("sendCaptcha");
    }
  },
  watch: {
    value (val) {
      this.currentValue = val
    },
    currentValue (newVal) {
      if (newVal !== "" && !this.isDisabled) {
        this.isClearShow = true;
      }else{
        this.isClearShow = false;
      }
      this.$emit('input', this.currentValue);
    }
  }
}
</script>

