<template>
  <div
    :class="['lc-cell', cellIndex=='first' && 'first-cell' , hasTopBorder && 'cell-top-border', hasBottomBorder && 'cell-bottom-border', desc && 'has-desc']"
    :style="cellStyle"
    :accessible="autoAccessible"
    :aria-label="`${label},${title},${desc}`"
    @click="cellClicked">

    <div class="line top-line" v-if="!hasTopBorder && cellIndex=='first'"></div>

    <slot name="label">
    </slot>
    <div v-if="label" class="cell-label">
      <image :src="icon" v-if="icon" :style="iconStyle" :class="['cell-label-icon', desc && 'is-desc']"></image>
      <div>
        <text class="cell-label-text" :style="labelStyle">{{label}}</text>
        <text class="cell-desc-text" v-if="desc" :style="descStyle">{{desc}}</text>
      </div>
    </div>
    

    <div class="cell-title">
      <slot name="title">
      </slot>
      <text v-if="title" class="cell-content" :style="titleStyle">{{title}}</text>
      
    </div>
    <slot name="value"></slot>
    <image :src="arrowIcon"
           class="cell-arrow-icon"
           :aria-hidden="true"
           v-if="hasArrow"></image>
    <div class="line bottom-line" v-if="!hasBottomBorder && cellIndex!=='last'"></div>           
  </div>
</template>

<style scoped>
  .lc-cell {
    position: relative;
    flex-direction: row;
    align-items: center;
    padding-top: 26px;
    padding-bottom: 26px;
    padding-left: 30px;
    padding-right: 30px;
    background-color: #ffffff;
  }
  .line {
    position: absolute;
    left: 30px;
    right: 0;
    height: 1px;
    background-color: #D7D7D7;
  }
  .top-line {
    top: 0;
  }
  .bottom-line {
    bottom: 0;
  }

  .has-desc {
    padding-bottom: 22px;
    padding-top: 22px;
  }

  .cell-top-border {
    border-top-color: #D7D7D7;
    border-top-width: 1px;
  }

  .cell-bottom-border {
    border-bottom-color: #D7D7D7;
    border-bottom-width: 1px;
  }

  .cell-label {
    margin-right: 40px;
    flex-direction: row;
    align-items: center;
  }
 
  .cell-label-icon {
    width: 60px;
    height: 60px;
    margin-right: 30px;
  }

  .is-desc {
    width: 72px;
    height: 72px;
  }

  .cell-label-text {
    font-size: 34px;
    color: #4A4A4A;
    line-height: 34px;
  }

  .cell-title {
    flex: 1;
    margin-right: 30px;
  }

  .cell-content {
    text-align: right;
    font-size: 30px;
    color: #7A7A7A;
    line-height: 30px; 
  }

  .cell-arrow-icon {
    width: 32px;
    height: 32px;
    margin-right: -8px;
  }

  .cell-desc-text {
    font-size: 26px;
    color: #787878;
    line-height: 30px;
    margin-top: 8px;
  }
</style>

<script>
  import Utils from '../utils';

  export default {
    props: {
      label: {
        type: String,
        default: ''
      },
      title: {
        type: String,
        default: ''
      },
      desc: {
        type: String,
        default: ''
      },
      link: {
        type: String,
        default: ''
      },
      hasTopBorder: {
        type: Boolean,
        default: true
      },
      hasBottomBorder: {
        type: Boolean,
        default: true
      },
      hasArrow: {
        type: Boolean,
        default: false
      },
      arrowIcon: {
        type: String,
        default: 'data:image/png;base64,'
      },
      cellStyle: {
        type: Object,
        default: () => ({})
      },
      labelStyle: {
        type: Object,
        default: () => ({})
      },
      descStyle: {
        type: Object,
        default: () => ({})
      },
      iconStyle: {
        type: Object,
        default: () => ({})
      },
      titleStyle: {
        type: Object,
        default: () => ({})
      },
      // titleTextStyle: {
      //   type: Object,
      //   default: () => ({})
      // },
      autoAccessible: {
        type: Boolean,
        default: true
      },
      icon: {
        type: String,
        default: ''
      },
      cellIndex: {
        type: String,
        default: ''
      }
    },
    methods: {
      cellClicked (e) {
        const link = this.link;
        this.$emit('LcCellClicked', { e });
        link && Utils.goToH5Page(link, true);
      }
    }
  };
</script>
