         <image class="fold-icon" :src="isFolded?foldIcon:expandIcon"></image>         
      </div>     
    </div>
    <slot v-if="!isFolded" name="foldContent"></slot>
  </div>
</template>

<script>
  export default {
    props:{
      title:{
        type: String,
        default: 'title'
      },
      desc:{
        type: String,
        default: 'desc'
      },
      folded:{
        type: Boolean,
        default: false
      }
    },
    data(){
