        expandIcon:'data:image/png;base64,'
      }
    },
    methods:{
      foldProcess(){
        this.isFolded=!this.isFolded;
        this.$emit("fold",this.isFolded);
      },
      moreList(){
          this.$emit("more",this.isFolded)
      }
    },
    created(){
      this.isFolded = this.folded;
    }
  }
</script>

