<template>
<div>
  <lc-cell :label="label"
    class="lc-input"
    :cell-style="cellStyle"
    :cell-index="cellIndex"
    :label-style="labelStyles"
    :title-style="titleStyle"
    :has-top-border="hasTopBorder"
    :has-bottom-border="hasBottomBorder"
    :accessible="true">
    <div slot="title">
      <!-- type=text -->
      <input
        v-if="!type || type === 'text'"
        :class="['lc-input__inner', showWarn && 'lc-input_warn', disabled && 'lc-input-disabled']"
        :style="inputStyle"
        :maxlength="max"
        :autofocus="autofocus"
        type="text"
        :name="name"
        :placeholder="placeholder"
        :disabled="disabled"
        v-model="currentValue"
        @focus="onFocus"
        @blur="onBlur"
        @return="onReturn"
        @input="onInput"
        @change="onChange"
        ref="input"/>
      <!-- type=date -->
      <input
        v-if="type === 'date'"
        :style="inputStyle"
        class="lc-input__inner"
        :autofocus="autofocus"
        type="date"
        :name="name"
        :placeholder="placeholder"
        :disabled="disabled"
        v-model="currentValue"
        @focus="onFocus"
        @blur="onBlur"
        @return="onReturn"
        @input="onInput"
        @change="onChange"
        ref="input"/>
      <!-- type=time -->
      <input
        v-if="type === 'time'"
        :style="inputStyle"
        class="lc-input__inner"
        :autofocus="autofocus"
        type="time"
        :name="name"
        :placeholder="placeholder"
        :disabled="disabled"
        v-model="currentValue"
        @focus="onFocus"
        @blur="onBlur"
        @return="onReturn"
        @input="onInput"
        @change="onChange"
        ref="input"/>
      <!-- type=number -->
      <input
        v-if="type === 'number'"
        :style="inputStyle"
        class="lc-input__inner"
        :maxlength="max"
        :autofocus="autofocus"
        type="number"
        :name="name"
        :placeholder="placeholder"
        :disabled="disabled"
        v-model="currentValue"
        @focus="onFocus"
        @blur="onBlur"
        @return="onReturn"
        @input="onInput"
        @change="onChange"
        ref="input"/>
      <!-- type=email -->
      <input
        v-if="type === 'email'"
        :style="inputStyle"
        class="lc-input__inner"
        :maxlength="max"
        :autofocus="autofocus"
        type="email"
        :name="name"
        :placeholder="placeholder"
        :disabled="disabled"
        v-model="currentValue"
        @focus="onFocus"
        @blur="onBlur"
        @return="onReturn"
        @input="onInput"
        @change="onChange"
        ref="input"/>
      <!-- type=url -->
      <input
        v-if="type === 'url'"
        :style="inputStyle"
        class="lc-input__inner"
        :maxlength="max"
        :autofocus="autofocus"
        type="url"
        :name="name"
        :placeholder="placeholder"
        :disabled="disabled"
        v-model="currentValue"
        @focus="onFocus"
        @blur="onBlur"
        @return="onReturn"
        @input="onInput"
        @change="onChange"
        ref="input"/>
      <!-- type=password -->
      <input
        v-if="type === 'password'"
        :style="inputStyle"
        class="lc-input__inner"
        :maxlength="max"
        :autofocus="autofocus"
        type="password"
        :name="name"
        :placeholder="placeholder"
        :disabled="disabled"
        v-model="currentValue"
        @focus="onFocus"
        @blur="onBlur"
        @return="onReturn"
        @input="onInput"
        @change="onChange"
        ref="input"/>
      <!-- type=tel -->
      <input
        v-if="type === 'tel'"
        :style="inputStyle"
        class="lc-input__inner"
        :maxlength="max"
        :autofocus="autofocus"
        type="tel"
        :name="name"
        :placeholder="placeholder"
        :disabled="disabled"
        v-model="currentValue"
        @focus="onFocus"
        @blur="onBlur"
        @return="onReturn"
        @input="onInput"
        @change="onChange"
        ref="input"/>
    </div>

    <div class="lc-input__ft" slot="value">
      <image style="width: 30px;height: 30px;" :src="checkIcon" @click="clear" v-if="showClear && currentValue && !disabled"></image>
      <!-- <i class="lc-input-icon lc-input-icon-warn" v-show="showWarn"></i>
      <i class="lc-input-icon lc-input-icon-warn" v-if="!novalidate && hasLengthEqual && equalWith && !valid"></i>
      <i  class="lc-input-icon lc-input-icon-success" v-show="!novalidate && equalWith && equalWith === currentValue && valid"></i> -->

      <slot name="right"></slot>
    </div>
  </lc-cell>

  <div class="error-msg" v-if="showWarn">
      <image style="width: 28px; height: 28px; margin-right: 20px;" :src="warnIcon"></image>
      <text class="error-text">{{ firstError }}</text>
    </div>
</div>
</template>


<script>

import LcCell from '../lc-cell';
import isEmail from 'validator/lib/isEmail'
import isMobilePhone from 'validator/lib/isMobilePhone'
import { CLEAR_ICON, WARN_ICON } from './type'

const validators = {
  'email': {
    fn: isEmail,
    msg: '邮箱'
  },
  'china-mobile': {
    fn (str) {
      var count = 1;
      if(count>0) {
        count = 0;
        return isMobilePhone(str, 'zh-CN')
      }   
    },
    msg: '手机号码'
  }
}


export default {
  components: { LcCell},
  created () {
    this.currentValue = (this.value === undefined || this.value === null) ? '' :  this.value

    if (this.required && typeof this.currentValue === 'undefined') {
      this.valid = false
    }
  },
  props: {
    label: String,
    type: {
      type: String,
      default: 'text'
    },
    value: [String, Number],
    name: String,
    placeholder: String,
    disabled: Boolean,
    autofocus: {
      type: Boolean,
      default: false
    },
    min: Number,
    max: Number,
    isType: [String, Function],
    showClear: {
      type: Boolean,
      default: true
    },
    novalidate: {
      type: Boolean,
      default: false
    },
    required: {
      type: Boolean,
      default: false
    },
    equalWith: String,
    labelAlign: String,    
    labelWidth: {
      type: String,
      default: '150px'
    },  
    inputStyle: {
      type: Object,
      default:()=>({})
    },
    cellIndex: {
      type: String,
      default: ''
    },
    cellStyle: {
        type: Object,
        default: () => ({})
    },
    titleStyle: {
        type: Object,
        default: () => ({marginRight:'0'})
    },
    hasTopBorder:{
        type: Boolean,
        default: true
    },
    hasBottomBorder:{
        type: Boolean,
        default: true
    },
    labelColor: {
      type: String,
      default: '#4A4A4A'
    }
  },
  computed: {
    labelStyles () {
      return {
        width: this.labelWidth || '',
        textAlign: this.labelAlign || 'left',
        color: this.disabled ? '#7A7A7A' : this.labelColor
      }
    },
    hasErrors () {
      return Object.keys(this.errors).length > 0
    },
    showWarn () {
      return !this.novalidate  && !this.valid && this.firstError && this.forceShowError;
    },
    checkIcon () {
      return CLEAR_ICON;
    },
    warnIcon () {
      return WARN_ICON;
    }
  },
  methods: {
    clear () {
      this.currentValue = ''
      this.focus()
    },
    focus () {
      this.$refs.input.focus()
    },
    blur () {
      this.$refs.input.blur()
    },
    onFocus ($event) {
      this.$emit('onFocus', this.currentValue, $event)
    },
    onBlur ($event) {
      this.validate()
      this.$emit('onBlur', this.currentValue, $event)
    },
    //weex不支持keyup事件
    onReturn ($event) {
      if ($event.key === 'Enter') {
        $event.target.blur()
        this.$emit('onEnter', this.currentValue, $event)
      }
    },
    onInput($event) {
      this.$emit('onInput', this.currentValue, $event)
    },
    onChange($event) {
      this.$emit('onChange', this.currentValue, $event)
    },
    getError () {
      let key = Object.keys(this.errors)[0]
      this.firstError = this.errors[key]
    },
    validate () {
      if(this.novalidate) {
        return;
      }

      if (typeof this.equalWith !== 'undefined') {
        this.validateEqual()
        return
      }
      this.errors = {}

      if (!this.currentValue && !this.required && !this.min) {
        this.valid = true
        return
      }
      if (!this.currentValue && this.required) {
        this.valid = false
        this.forceShowError = true  //new
        this.errors.required = '必填'
        this.getError()
        return
      }

      if (typeof this.isType === 'string') {
        const validator = validators[this.isType]
        if (validator) {
          let value = this.currentValue

          this.valid = validator[ 'fn' ](value)
          if (!this.valid) {
            this.forceShowError = true
            this.errors.format = validator[ 'msg' ] + '格式错误'
            this.getError()
            return
          } else {
            delete this.errors.format
          }
        }
      }

      if (typeof this.isType === 'function') {
        const validStatus = this.isType(this.currentValue)
        this.valid = validStatus.valid
        if (!this.valid) {
          this.errors.format = validStatus.msg
          this.forceShowError = true
          if (!this.firstError) {
            this.getError()
          }
          return
        } else {
          delete this.errors.format
        }
      }

      if (this.min) {
        if (this.currentValue.length < this.min) {
          this.errors.min = `最少应该输入${this.min}个字符`
          this.valid = false
          if (!this.firstError) {
            this.getError()
          }
          this.forceShowError = true//新增
          return
        } else {
          this.forceShowError = false
          delete this.errors.min
        }
      }

      if (this.max) {
        if (this.currentValue.length > this.max) {
          this.errors.max = `最多可以输入${this.max}个字符`
          this.valid = false
          this.forceShowError = true
          return
        } else {
          this.forceShowError = false
          delete this.errors.max
        }
      }

      this.valid = true
    },

    validateEqual () {
      if (!this.equalWith && this.currentValue) {
        this.valid = false
        this.errors.equal = '输入不一致'
        return
      }
  
      if (this.currentValue !== this.equalWith) {
        this.valid = false;
        this.forceShowError = true
        this.errors.equal = '输入不一致'
        return
      } else {
        if (!this.currentValue && this.required) {
          this.valid = false
        } else {
          this.valid = true
          delete this.errors.equal
          // this.errors.equal = {}
        }
      }
    }
  },
  data: () => ({ 
    firstError: '',
    forceShowError: false,
    valid: true,
    currentValue: '',
    errors: {}
  }),
  watch: {
    valid () {
      this.getError()
    },
    value (val) {
      this.currentValue = val
    },
    equalWith (newVal) {
      // if (newVal && this.equalWith) {
      //   if (newVal.length === this.equalWith.length) {
      //     this.hasLengthEqual = true
      //   }
      //   this.validateEqual()
      // } else {
      //   this.validate()
      // }
    },
    currentValue (newVal) {
      //去掉了!
      // if (!this.equalWith && newVal) {
      //   this.validateEqual()
      // }
      // if (newVal!=='' && this.equalWith) {
      //   if (newVal.length === this.equalWith.length) {
      //     this.hasLengthEqual = true
      //   }
      //   this.validateEqual()
      // } else {
      //   this.validate()
      // }
      // this.$emit('on-change', newVal)
      this.$emit('input', newVal);

    }
  }
}
</script>

