<template>
  <div class="lc-stepper">
    <div class="stepper-minus"
         @click="minusClicked"
         aria-label="减数"
         :accessible="true">
      <text class="stepper-icon" :style="{ color: isLess?'#cccccc':'#666666' }">-</text>
    </div>
    <input class="stepper-input"
           ref="input"
           type="number"
           v-model="valueString"
           @input="onInput"
           @focus="onFocus"
           @blur="onBlur"
           :style="disableStyle"
           :disabled="disabled||readOnly"/>
    <div class="stepper-plus"
         @click="plusClicked"
         aria-label="加数"
         :accessible="true">
      <text class="stepper-icon" :style="{ color: isOver ? '#cccccc': '#666666' }">+</text>
    </div>
  </div>
</template>

<style scoped>
  .lc-stepper {
    flex-direction: row;
  }

  .stepper-plus, .stepper-minus {
    width: 56px;
    height: 56px;
    background-color: #ededed;
    align-items: center;
    justify-content: center;
    border-radius: 6px;
  }

  .stepper-input {
    border-width: 0;
    outline: none;
    text-align: center;
    color: #3d3d3d;
    font-size: 30px;
    line-height: 56px;
    width: 86px;
  }

  .stepper-icon {
    font-size: 36px;
    color: #666666;
    margin-top: -4px;
  }

</style>

<script>
const modal = weex.requireModule('modal');
import Utils from '../utils';

  export default {
    props: {
      min: {
        type: [String, Number],
        default: 1
      },
      max: {
        type: [String, Number],
        default: 100
      },
      step: {
        type: [String, Number],
        default: 1
      },
      disabled: {
        type: Boolean,
        default: false
      },
      defaultValue: {
        type: [String, Number],
        default: 1
      },
      readOnly: {
        type: Boolean,
        default: false
      }
    },
    computed: {
      disableStyle () {
        if (this.disabled) {
          return {
            color: '#cccccc'
          }
        }
      },
      // valueString () {
      //   return this.value.toString();
      // }
    },
    watch: {
        //123389 lc-stepper组件中间input输入时，max值设置会无效
      value(val) {
        // debugger
        this.valueString = val;
      }
    },
    data: () => ({
      value: 1,
      isLess: false,
      isOver: false,
      valueString: null,
      validValue: null
    }),
    created () {
      const self = this;
      // self.value = parseInt(self.defaultValue, 10);
      self.value = parseFloat(self.defaultValue);

      if (self.disabled) {
        self.isLess = true;
        self.isOver = true;
        return;
      }
      if (self.value >= self.max) {
        self.isOver = true;
      }
      if(self.value <= self.min) {
        self.isLess = true;
      }

    },
    methods: {
      minusClicked () {
        const self = this;
        if (self.disabled) {
          return;
        }
        const isMinOver = self.value <= self.min;
        const nowNum = self.value - parseInt(self.step, 10);
        if (isMinOver) {
          self.$emit('LcStepperValueIsMinOver', { value: self.value });
        } else {
          self.value = nowNum;
          self.resetDisabledStyle();
        }
        // 由于此处已经减step
        if (nowNum <= self.min) {
          self.value = parseInt(self.min, 10);
          self.isLess = true;
        }
        self.$emit('LcStepperValueChanged', { value: self.value });
      },
      plusClicked () {
        const self = this;
        if (self.disabled) {
          return;
        }
        const isMaxOver = self.value >= self.max;
        const nowNum = self.value + parseInt(self.step, 10);
        if (isMaxOver) {
          self.$emit('LcStepperValueIsMaxOver', { value: self.value });
        } else {
          self.value = nowNum;
          self.resetDisabledStyle();
        }
        // 由于此处已经加step
        if (nowNum >= self.max) {
          self.value = parseInt(self.max, 10);
          self.isOver = true;
        }
        self.$emit('LcStepperValueChanged', { value: self.value });
      },
      onInput (e) {
        // this.correctInputValue(e.value);
      },
      onFocus(e) {
          //123389 lc-stepper组件中间input输入时，max值设置会无效
        if(Utils.env.isWeb()){
            if(e.value==0) {
                this.valueString = '';
                this.value = '';
            }
        }else{
            if(e.target.attr.value==0) {
                this.valueString = '';
                this.value = '';
            }
        }
        
      },
      onBlur (e) {
        this.correctInputValue(e);
      },
      correctInputValue (e) {
        const self = this;
        let v;
        //123389 lc-stepper组件中间input输入时，max值设置会无效
        if(Utils.env.isWeb()){
            v = e.value;
        }else{
            v = e.target.attr.value;
        }
        
        let reg = /^\d+(\.(\d{1,2}))?$/;
        if(reg.test(v)) {
          if(parseFloat(v) >= self.min && parseFloat(v) <= self.max) {
            self.value = parseFloat(v);
            this.validValue = self.value;
          } else {
            this.valueString = this.validValue;
            this.value = this.validValue;

          }
        } else {
          this.valueString = this.validValue;
          this.value = this.validValue;

        }
        self.$emit('LcStepperValueChanged', { value: self.value });
      },

      resetDisabledStyle () {
        this.isLess = false;
        this.isOver = false;
      }
    }
  };
</script>
